import esp import json import urllib.request STARSHIPS_URL = 'https://swapi.dev/api/starships/' # Star Wars Starships next_page = f"{STARSHIPS_URL}?page=1"
def publish(): global next_page esp.logMessage(logcontext="my_connector", message=f'Making API call to {next_page}', level="info") response = urllib.request.urlopen(next_page) response = response.read().decode('utf-8') response = json.loads(response)
events = [] for result in response['results']: keep_keys = ['name', 'model', 'manufacturer', 'cost_in_credits', 'length', 'max_atmosphering_speed', 'crew', 'passengers', 'cargo_capacity', 'consumables', 'hyperdrive_rating', 'MGLT', 'starship_class'] event = dict((k, result[k]) for k in keep_keys if k in result) events.append(event)
done = False next_page = response['next'] if next_page is None: done = True esp.logMessage(logcontext="my_connector", message=f'Connector finished', level="info")
return { "events": events, "done": done }
